interface FilterOptions {
  category: ItemCategory | 'all';
  colors: string[];
  brands: string[];
  hasAIName: boolean | null;
  searchQuery: string;
}

interface SortOptions {
  field: 'name' | 'category' | 'createdAt' | 'lastWorn' | 'wearCount';
  direction: 'asc' | 'desc';
}

const CATEGORIES: (ItemCategory | 'all')[] = [
