  async calculateCostPerWear(itemId: string): Promise<number> {
    try {
      const stats = await this.getItemUsageStats(itemId);
      return stats.costPerWear;
    } catch (error) {
      errorInDev(
        '[EnhancedWardrobeService] Failed to calculate cost per wear:',
